import React from "react";

import Titles from "./components/Titles"
import Form from "./components/Form"
import Weather from "./components/Weahter"



const API_KEY = "e4fc0603b3aa74788cce8f69d0c06846";


//wrapper component
//This class creates an instance of a react component and the will name it App
//It is also the major component
class App extends React.Component{

getWeather = async() => {

  const api_call = await fetch('http://api.openweathermap.org/data/2.5/weather?q=Manchester,uk&appid=${API_KEY}&units=metric');

  const data = await api_call.json();

  console.log(data);
}


//It returns a JSX
render(){

  //In JSX u can only return one pair of element
    return (
      <div>
        <Titles />

        <Form getWeather = {this.getWeather}/>

        <Weather />

      </div>
    );

  }

};

//Make this file (App) available to other files
export default App;
